mapper.insert:
insert into hotel(ht_name, ht_price, pk_no)
values(#{htName},#{htPrice}, #{pkNo});

insert into airplane(ap_price, ap_arrival, ap_departure, pk_no)
values(#{apPrice},#{apArrival},#{apDeparture}, #{pkNo});

insert into festival(fv_name,fv_price, pk_no)
values(#{fvName},#{fvPrice},#{pkNo});

insert into place(pl_country,pl_name,pk_no, pl_continent)
values(#{plCountry},#{plName}, #{pkNo}, #{plContinent});

mapper.update:

update package set pk_name = #{pkName}, pk_content = #{pkContent} ,pk_continent = #{pkContinent},pk_price = #{pkPrice}  where pk_no = #{pkNo};	
update hotel set ht_name = #{htName} , ht_price = #{htPrice} where pk_no = #{pkNo};
update airplane set ap_price = #{apPrice} , ap_arrival = #{apArrival}, ap_departure = #{apDeparture} where pk_no = #{pkNo};
update place set pl_name = #{plName} , pl_country = #{plCountry} , pl_continent = #{plContinent} where pk_no = #{pkNo};
update festival set fv_name = #{fvName} , fv_price = #{fvPrice} where pk_no = #{pkNo};

mapper.delete:
delete from package where pk_no = #{pkNo};
delete from place where pk_no = #{pkNo};
delete from hotel where pk_no = #{pkNo};
delete from airplane where pk_no = #{pkNo};
delete from festival where pk_no = #{pkNo};
